visualization.dot_content:
// BFIH Evidence Flow: Does moderate alcohol consumption provide cardiovascular hea...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Does moderate alcohol consumption provide cardiovascular\nhealth benefits?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen explanation\n\nPrior: 0.05\nPosterior: 0.030\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=1];

        h1 [label="H1: TRUE - Moderate alcohol is\ncardioprotective\n\nPrior: 0.20\nPosterior: 0.048\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h2 [label="H2: FALSE - No cardiovascular benefit\n\nPrior: 0.25\nPosterior: 0.262\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=2];

        h3 [label="H3: PARTIAL - Dose and pattern\ndependent\n\nPrior: 0.25\nPosterior: 0.275\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2.5];

        h4 [label="H4: PARTIAL - Confounded by lifestyle\n\nPrior: 0.15\nPosterior: 0.287\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=3];

        h5 [label="H5: PARTIAL - Beverage-type specific\n\nPrior: 0.10\nPosterior: 0.098\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: Interventional & mechanistic biomarker\neffects\n(6 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="Experimental/interventional and\nphysiologic findings (e.g., HDL,\nApoA1, lipid subfractions,\n(6 items)\n\nWoE to H1: 1.0 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Observational cardioprotection &\nJ-shaped associations\n(19 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="Prospective cohorts/meta-analyses\nand institutional summaries\nreporting lower CVD\nevents/mortality\n(19 items)\n\nWoE to H4: 0.6 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Causal inference &\nconfounding/selection critiques\n(10 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="Evidence emphasizing\nconfounding/selection (sick\nquitter, healthy-user), lifestyle\nattenuation, and\n(10 items)\n\nWoE to H4: 1.3 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    subgraph cluster_c4 {
        label="C4: Harms, risk-factor elevation, and\nguideline positions\n(8 items)";
        style="filled,rounded";
        fillcolor="#E6F3FF";
        fontsize=10;

        c4_node [label="Evidence emphasizing harms or lack\nof proven causal benefit (blood\npressure increases, acute risk,\n(8 items)\n\nWoE to H2: 1.2 dB",
                 shape=ellipse, style="filled", fillcolor="#CCE6FF"];
    }

    subgraph cluster_c5 {
        label="C5: Beverage-type specificity\n(wine/champagne vs beer/spirits)\n(2 items)";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=10;

        c5_node [label="Evidence claiming differential\ncardiovascular associations by\nbeverage type (wine/champagne/white\n(2 items)\n\nWoE to H5: 2.6 dB",
                 shape=ellipse, style="filled", fillcolor="#E6CCFF"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h1 [label="LR: 1.26", color="#66CC66", penwidth=2, style=solid];
    c1_node -> h2 [label="LR: 0.85", color="#999999", penwidth=1, style=dashed];
    c2_node -> h4 [label="LR: 1.16", color="#999999", penwidth=1, style=dashed];
    c2_node -> h5 [label="LR: 0.71", color="#CC6666", penwidth=1, style=dotted];
    c3_node -> h1 [label="LR: 0.47", color="#CC0000", penwidth=1.5, style=dotted];
    c3_node -> h4 [label="LR: 1.35", color="#66CC66", penwidth=2, style=solid];
    c4_node -> h1 [label="LR: 0.49", color="#CC0000", penwidth=1.5, style=dotted];
    c4_node -> h2 [label="LR: 1.31", color="#66CC66", penwidth=2, style=solid];
    c5_node -> h1 [label="LR: 0.62", color="#CC6666", penwidth=1, style=dotted];
    c5_node -> h5 [label="LR: 1.82", color="#66CC66", penwidth=2, style=solid];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH4: 28.7%\nH3: 27.5%\nH2: 26.2%\nH5: 9.8%\nH1: 4.8%\n\nVerdict: UNCERTAIN",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=dashed];
    h1 -> posterior_summary [style=dashed];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=solid];
    h4 -> posterior_summary [style=solid];
    h5 -> posterior_summary [style=dashed];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Scientific Cons\nH4: 28.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Traditional/Cul\nH3: 42.8%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Strict Precauti\nH4: 45.4%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 45\nEvidence Clusters: 5\nParadigms Analyzed: 3\nAvg Items/Cluster: 9.0\n\nCoverage: Substantial",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH4: 15% → 28.7% (+13.7%)\nH3: 25% → 27.5% (+2.5%)\nH2: 25% → 26.2% (+1.2%)\n\nTotal WoE: 28.0 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 2 different conclusions across paradigms\n• 0 strong support signals\n• 0 strong refutation signals\n• Narrow margin (1%) - some uncertainty",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H4\n(PARTIAL - Confounded by\nlifestyle)\n\nPosterior: 28.7%\nMargin over #2: 1.2%\nConfidence: Low\n\nStatus: UNCERTAIN",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
