// BFIH Evidence Flow: Does homeopathy work better than placebo for treating medica...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Does homeopathy work better than placebo for treating\nmedical conditions?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen explanation\n\nPrior: 0.05\nPosterior: 0.087\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=1];

        h1 [label="H1: TRUE - Homeopathy has specific\ntherapeutic effects\n\nPrior: 0.05\nPosterior: 0.068\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h2 [label="H2: FALSE - No effect beyond placebo\n\nPrior: 0.60\nPosterior: 0.375\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=3];

        h3 [label="H3: PARTIAL - Works for some\nconditions only\n\nPrior: 0.10\nPosterior: 0.196\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2.5];

        h4 [label="H4: PARTIAL - Therapeutic ritual\neffect\n\nPrior: 0.15\nPosterior: 0.110\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2];

        h5 [label="H5: PARTIAL - Low-dose hormesis\n\nPrior: 0.05\nPosterior: 0.165\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: High-level clinical reviews &\ninstitutional/consensus skepticism\n(20 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="Large meta-analyses,\nCochrane/major governmental or\nacademy statements, and mainstream\nsummaries\n(20 items)\n\nWoE to H2: 1.2 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Clinical evidence reporting benefit\nbeyond placebo (meta-analyses & RCTs)\n(15 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="Meta-analyses and controlled\ntrials reporting statistically\nsignificant benefit of homeopathic\n(15 items)\n\nWoE to H1: 4.1 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Consultation/ritual and placebo-context\neffects\n(9 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="Evidence that improvements\ncorrelate with consultation\nquality, expectation management,\nand\n(9 items)\n\nWoE to H4: 0.8 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    subgraph cluster_c4 {
        label="C4: Mechanistic claims: water memory /\nphysicochemical signatures / anomalous\neffects\n(7 items)";
        style="filled,rounded";
        fillcolor="#E6F3FF";
        fontsize=10;

        c4_node [label="Claims and studies suggesting\nultradilutions/succussion leave\nmeasurable physicochemical\nsignatures\n(7 items)\n\nWoE to H1: 2.8 dB",
                 shape=ellipse, style="filled", fillcolor="#CCE6FF"];
    }

    subgraph cluster_c5 {
        label="C5: Hormesis / nanoparticles / low-dose\nmechanism debate\n(6 items)";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=10;

        c5_node [label="Evidence and commentary proposing\n(or criticizing)\nhormesis/nanoparticle mechanisms as\na partial\n(6 items)\n\nWoE to H5: 1.9 dB",
                 shape=ellipse, style="filled", fillcolor="#E6CCFF"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h1 [label="LR: 0.38", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h2 [label="LR: 1.31", color="#66CC66", penwidth=2, style=solid];
    c2_node -> h1 [label="LR: 2.55", color="#228B22", penwidth=2.5, style=solid];
    c2_node -> h2 [label="LR: 0.52", color="#CC6666", penwidth=1, style=dotted];
    c2_node -> h3 [label="LR: 2.25", color="#228B22", penwidth=2.5, style=solid];
    c3_node -> h3 [label="LR: 0.86", color="#999999", penwidth=1, style=dashed];
    c3_node -> h4 [label="LR: 1.19", color="#999999", penwidth=1, style=dashed];
    c4_node -> h1 [label="LR: 1.90", color="#66CC66", penwidth=2, style=solid];
    c4_node -> h2 [label="LR: 0.66", color="#CC6666", penwidth=1, style=dotted];
    c5_node -> h2 [label="LR: 0.79", color="#CC6666", penwidth=1, style=dotted];
    c5_node -> h5 [label="LR: 1.55", color="#66CC66", penwidth=2, style=solid];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH2: 37.5%\nH3: 19.6%\nH5: 16.5%\nH4: 11.0%\nH0: 8.7%\n\nVerdict: PARTIALLY VALIDATED",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=dashed];
    h1 -> posterior_summary [style=dashed];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=solid];
    h4 -> posterior_summary [style=solid];
    h5 -> posterior_summary [style=solid];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Scientific Cons\nH2: 37.5%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Integrative Med\nH1: 55.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Skeptical Empir\nH2: 78.1%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 57\nEvidence Clusters: 5\nParadigms Analyzed: 3\nAvg Items/Cluster: 11.4\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH2: 60% → 37.5% (-22.5%)\nH3: 10% → 19.6% (+9.6%)\nH5: 5% → 16.5% (+11.5%)\n\nTotal WoE: 43.2 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 2 different conclusions across paradigms\n• 0 strong support signals\n• 0 strong refutation signals\n• Narrow margin (18%) - some uncertainty",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H2\n(FALSE - No effect beyond\nplacebo)\n\nPosterior: 37.5%\nMargin over #2: 17.9%\nConfidence: Low\n\nStatus: PARTIALLY VALIDATED",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
