visualization.dot_content:
// BFIH Evidence Flow: Do vaccines cause autism in children?...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Do vaccines cause autism in children?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (8 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen primary driver\n\nPrior: 0.10\nPosterior: 0.009\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=1];

        h1 [label="H1: TRUE - Institutional distrust &\npsychological heuristics\n\nPrior: 0.25\nPosterior: 0.287\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=3];

        h2 [label="H2: TRUE - Algorithmic misinformation\namplification\n\nPrior: 0.20\nPosterior: 0.253\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=2.5];

        h3 [label="H3: TRUE - Cultural & historical\nanti-establishment narratives\n\nPrior: 0.10\nPosterior: 0.116\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=2];

        h4 [label="H4: TRUE - Educational and\nscience-literacy gaps\n\nPrior: 0.10\nPosterior: 0.059\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h5 [label="H5: TRUE - Legacy pharmaceutical\nmisconduct fuels rational\nskepticism\n\nPrior: 0.07\nPosterior: 0.083\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h6 [label="H6: TRUE - Theological and moral\nobjections to perceived unnatural\nintervention\n\nPrior: 0.05\nPosterior: 0.035\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h7 [label="H7: TRUE - Economic incentives of the\nalternative-medicine industry\n\nPrior: 0.13\nPosterior: 0.156\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=2];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: High-quality epidemiology: No causal\nlink\n(8 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="Meta-analyses, large cohort\nstudies and authoritative reviews\nconsistently find no association\n(8 items)\n\nWoE to H1: 0.5 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Digital misinformation dynamics\n(6 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="Empirical studies on algorithmic\namplification and social-media\nspread of anti-vaccine/autism\n(6 items)\n\nWoE to H2: 2.2 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Institutional trust & vaccine hesitancy\n(8 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="Surveys and experiments linking\nlow trust in health/pharma\ninstitutions to vaccine hesitancy\n(8 items)\n\nWoE to H1: 1.9 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    subgraph cluster_c4 {
        label="C4: Historical scandals & anti-vaccine\nlineage\n(8 items)";
        style="filled,rounded";
        fillcolor="#E6F3FF";
        fontsize=10;

        c4_node [label="Historical events of medical or\npharmaceutical wrongdoing and\nlong-standing anti-vaccination\n(8 items)\n\nWoE to H3: 0.9 dB",
                 shape=ellipse, style="filled", fillcolor="#CCE6FF"];
    }

    subgraph cluster_c5 {
        label="C5: Religious, moral & commercial\necosystems\n(8 items)";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=10;

        c5_node [label="Evidence on theological\nobjections, moral world-views and\nmonetised alternative-health\nindustries\n(8 items)\n\nWoE to H7: 1.2 dB",
                 shape=ellipse, style="filled", fillcolor="#E6CCFF"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h0 [label="LR: 0.20", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h1 [label="LR: 1.12", color="#999999", penwidth=1, style=dashed];
    c2_node -> h1 [label="LR: 0.76", color="#CC6666", penwidth=1, style=dotted];
    c2_node -> h2 [label="LR: 1.65", color="#66CC66", penwidth=2, style=solid];
    c3_node -> h0 [label="LR: 0.73", color="#CC6666", penwidth=1, style=dotted];
    c3_node -> h1 [label="LR: 1.54", color="#66CC66", penwidth=2, style=solid];
    c4_node -> h0 [label="LR: 0.85", color="#999999", penwidth=1, style=dashed];
    c4_node -> h3 [label="LR: 1.23", color="#66CC66", penwidth=2, style=solid];
    c5_node -> h0 [label="LR: 0.78", color="#CC6666", penwidth=1, style=dotted];
    c5_node -> h7 [label="LR: 1.33", color="#66CC66", penwidth=2, style=solid];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH1: 28.7%\nH2: 25.3%\nH7: 15.6%\nH3: 11.6%\nH5: 8.3%\n\nVerdict: UNCERTAIN",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=dashed];
    h1 -> posterior_summary [style=solid];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=solid];
    h4 -> posterior_summary [style=dashed];
    h5 -> posterior_summary [style=dashed];
    h6 -> posterior_summary [style=dashed];
    h7 -> posterior_summary [style=solid];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Integrated Evid\nH1: 28.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k0_inv [label="K0-inv: Covenantal Stew\nH6: 23.2%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Industry-Centri\nH1: 40.8%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Anecdote-Driven\nH5: 42.2%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k3 [label="K3: Libertarian Med\nH3: 56.4%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k4 [label="K4: Precautionary F\nH4: 22.3%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k0_inv [style=dashed];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];
    posterior_summary -> paradigm_k3 [style=dashed];
    posterior_summary -> paradigm_k4 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 72\nEvidence Clusters: 5\nParadigms Analyzed: 6\nAvg Items/Cluster: 14.4\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH1: 25% → 28.7% (+3.7%)\nH2: 20% → 25.3% (+5.3%)\nH7: 13% → 15.6% (+2.6%)\n\nTotal WoE: 35.3 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k0_inv -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k3 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k4 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 5 different conclusions across paradigms\n• 0 strong support signals\n• 1 strong refutation signals\n• Narrow margin (3%) - some uncertainty",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H1\n(TRUE - Institutional distrust\n& psychological heuristics)\n\nPosterior: 28.7%\nMargin over #2: 3.4%\nConfidence: Low\n\nStatus: UNCERTAIN",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
