// BFIH Evidence Flow: Do vaccines cause autism in children?...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Do vaccines cause autism in children?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen explanation\n\nPrior: 0.05\nPosterior: 0.167\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=3];

        h1 [label="H1: TRUE - Vaccines directly cause\nautism\n\nPrior: 0.05\nPosterior: 0.167\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=3];

        h2 [label="H2: FALSE - No causal link exists\n\nPrior: 0.60\nPosterior: 0.167\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=3];

        h3 [label="H3: PARTIAL - Rare subgroup\nvulnerability\n\nPrior: 0.10\nPosterior: 0.167\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=3];

        h4 [label="H4: PARTIAL - Indirect pathway via\nimmune activation\n\nPrior: 0.10\nPosterior: 0.167\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=3];

        h5 [label="H5: PARTIAL - Timing/schedule effects\nonly\n\nPrior: 0.10\nPosterior: 0.167\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=3];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================


    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH0: 16.7%\nH1: 16.7%\nH2: 16.7%\nH3: 16.7%\nH4: 16.7%\n\nVerdict: UNCERTAIN",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=solid];
    h1 -> posterior_summary [style=solid];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=solid];
    h4 -> posterior_summary [style=solid];
    h5 -> posterior_summary [style=solid];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Scientific Cons\nH0: 16.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Vaccine Skeptic\nH0: 16.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Precautionary\nH0: 16.7%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 51\nEvidence Clusters: 0\nParadigms Analyzed: 3\nAvg Items/Cluster: 0.0\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH0: 5% → 16.7% (+11.7%)\nH1: 5% → 16.7% (+11.7%)\nH2: 60% → 16.7% (-43.3%)\n\nTotal WoE: 0.0 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• All 3 paradigms agree on H0\n• 0 strong support signals\n• 0 strong refutation signals\n• Narrow margin (0%) - some uncertainty",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H0\n(OTHER - Unforeseen\nexplanation)\n\nPosterior: 16.7%\nMargin over #2: 0.0%\nConfidence: Low\n\nStatus: UNCERTAIN",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
