// BFIH Evidence Flow: Do vaccines cause autism in children?...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Do vaccines cause autism in children?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen explanation\n\nPrior: 0.05\nPosterior: 0.055\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=1];

        h1 [label="H1: TRUE - Vaccines directly cause\nautism\n\nPrior: 0.05\nPosterior: 0.003\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h2 [label="H2: FALSE - No causal link exists\n\nPrior: 0.60\nPosterior: 0.282\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=2.5];

        h3 [label="H3: PARTIAL - Rare subgroup\nvulnerability\n\nPrior: 0.10\nPosterior: 0.235\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2];

        h4 [label="H4: PARTIAL - Indirect pathway via\nimmune activation\n\nPrior: 0.10\nPosterior: 0.411\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=3];

        h5 [label="H5: PARTIAL - Timing/schedule effects\nonly\n\nPrior: 0.10\nPosterior: 0.015\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: Large-scale human epidemiology (null\nassociation)\n(17 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="Meta-analyses and nationwide\ncohort/case-control studies\nconsistently find no statistically\n(17 items)\n\nWoE to H2: 4.2 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Authoritative consensus & institutional\nreviews\n(13 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="IOM, CDC, WHO, Cochrane and other\nbodies repeatedly conclude no\ncausal link between vaccines and\n(13 items)\n\nWoE to H2: 2.9 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Biological & animal-model mechanism\nevidence\n(7 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="Laboratory and animal studies\nshowing immune activation or\naluminum adjuvant neurotoxicity\n(7 items)\n\nWoE to H4: 6.8 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    subgraph cluster_c4 {
        label="C4: Positive association & rare-case\nsignals\n(4 items)";
        style="filled,rounded";
        fillcolor="#E6F3FF";
        fontsize=10;

        c4_node [label="Small studies, legal cases,\nre-analyses or policy notices\nsuggesting a possible\nvaccine-autism link\n(4 items)\n\nWoE to H1: 6.3 dB",
                 shape=ellipse, style="filled", fillcolor="#CCE6FF"];
    }

    subgraph cluster_c5 {
        label="C5: Schedule & antigen-load null studies\n(3 items)";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=10;

        c5_node [label="Studies examining vaccine timing,\nantigen load or cumulative schedule\nthat find no association with\n(3 items)\n\nWoE to H2: 3.8 dB",
                 shape=ellipse, style="filled", fillcolor="#E6CCFF"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h1 [label="LR: 0.07", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h2 [label="LR: 2.62", color="#228B22", penwidth=2.5, style=solid];
    c1_node -> h5 [label="LR: 0.31", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h1 [label="LR: 0.08", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h2 [label="LR: 1.97", color="#66CC66", penwidth=2, style=solid];
    c3_node -> h2 [label="LR: 0.25", color="#CC0000", penwidth=1.5, style=dotted];
    c3_node -> h4 [label="LR: 4.74", color="#006600", penwidth=3, style=solid];
    c4_node -> h1 [label="LR: 4.27", color="#006600", penwidth=3, style=solid];
    c4_node -> h2 [label="LR: 0.10", color="#CC0000", penwidth=1.5, style=dotted];
    c4_node -> h3 [label="LR: 3.15", color="#006600", penwidth=3, style=solid];
    c5_node -> h2 [label="LR: 2.42", color="#228B22", penwidth=2.5, style=solid];
    c5_node -> h5 [label="LR: 0.18", color="#CC0000", penwidth=1.5, style=dotted];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH4: 41.1%\nH2: 28.2%\nH3: 23.5%\nH0: 5.5%\nH5: 1.5%\n\nVerdict: PARTIALLY VALIDATED",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=dashed];
    h1 -> posterior_summary [style=dashed];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=solid];
    h4 -> posterior_summary [style=solid];
    h5 -> posterior_summary [style=dashed];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Scientific Cons\nH4: 41.1%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Vaccine Skeptic\nH4: 38.1%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Precautionary\nH1: 49.3%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 58\nEvidence Clusters: 5\nParadigms Analyzed: 3\nAvg Items/Cluster: 11.6\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH4: 10% → 41.1% (+31.1%)\nH2: 60% → 28.2% (-31.8%)\nH3: 10% → 23.5% (+13.5%)\n\nTotal WoE: 112.4 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 2 different conclusions across paradigms\n• 3 strong support signals\n• 6 strong refutation signals\n• Narrow margin (13%) - some uncertainty",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H4\n(PARTIAL - Indirect pathway\nvia immune activation)\n\nPosterior: 41.1%\nMargin over #2: 12.9%\nConfidence: Low\n\nStatus: PARTIALLY VALIDATED",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
