// BFIH Evidence Flow: Do vaccines cause autism in children?...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Do vaccines cause autism in children?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen explanation\n\nPrior: 0.05\nPosterior: 0.315\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=2.5];

        h1 [label="H1: TRUE - Vaccines directly cause\nautism\n\nPrior: 0.05\nPosterior: 0.000\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h2 [label="H2: FALSE - No causal link exists\n\nPrior: 0.60\nPosterior: 0.677\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=3];

        h3 [label="H3: PARTIAL - Rare subgroup\nvulnerability\n\nPrior: 0.10\nPosterior: 0.005\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

        h4 [label="H4: PARTIAL - Indirect pathway via\nimmune activation\n\nPrior: 0.10\nPosterior: 0.002\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

        h5 [label="H5: PARTIAL - Timing/schedule effects\nonly\n\nPrior: 0.10\nPosterior: 0.001\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: Large-scale Epidemiological Null\nFindings\n(24 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="High-quality cohort, case-control,\nand meta-analysis studies (large N,\nrigorous peer-review)\n(24 items)\n\nWoE to H2: 6.7 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Institutional Consensus Statements\n(14 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="Statements and reviews by IOM,\nCDC, WHO, AAP and other\nauthoritative bodies concluding\nvaccines are\n(14 items)\n\nWoE to H2: 6.8 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Positive Association Claims & Schedule\nConcerns\n(8 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="Lower-quality or highly contested\nstudies and policy signals\nsuggesting vaccines (or schedule\n(8 items)\n\nWoE to H0: 4.8 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    subgraph cluster_c4 {
        label="C4: Retractions & Fraud Findings\n(3 items)";
        style="filled,rounded";
        fillcolor="#E6F3FF";
        fontsize=10;

        c4_node [label="Documentation that key pro-link\nstudies (e.g., Wakefield 1998) were\nretracted for fraud and ethics\n(3 items)\n\nWoE to H2: 5.1 dB",
                 shape=ellipse, style="filled", fillcolor="#CCE6FF"];
    }

    subgraph cluster_c5 {
        label="C5: Biological Mechanism Speculations\n(Maternal Immune Activation)\n(1 items)";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=10;

        c5_node [label="Animal and translational work\nsuggesting maternal immune\nactivation can induce autism-like\n(1 items)\n\nWoE to H4: 3.7 dB",
                 shape=ellipse, style="filled", fillcolor="#E6CCFF"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h1 [label="LR: 0.08", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h2 [label="LR: 4.65", color="#006600", penwidth=3, style=solid];
    c1_node -> h3 [label="LR: 0.30", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h4 [label="LR: 0.15", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h5 [label="LR: 0.15", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h1 [label="LR: 0.08", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h2 [label="LR: 4.80", color="#006600", penwidth=3, style=solid];
    c2_node -> h3 [label="LR: 0.23", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h4 [label="LR: 0.15", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h5 [label="LR: 0.15", color="#CC0000", penwidth=1.5, style=dotted];
    c3_node -> h0 [label="LR: 2.99", color="#228B22", penwidth=2.5, style=solid];
    c3_node -> h2 [label="LR: 0.28", color="#CC0000", penwidth=1.5, style=dotted];
    c4_node -> h1 [label="LR: 0.31", color="#CC0000", penwidth=1.5, style=dotted];
    c4_node -> h2 [label="LR: 3.24", color="#006600", penwidth=3, style=solid];
    c4_node -> h4 [label="LR: 0.23", color="#CC0000", penwidth=1.5, style=dotted];
    c4_node -> h5 [label="LR: 0.23", color="#CC0000", penwidth=1.5, style=dotted];
    c5_node -> h2 [label="LR: 0.59", color="#CC6666", penwidth=1, style=dotted];
    c5_node -> h4 [label="LR: 2.36", color="#228B22", penwidth=2.5, style=solid];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH2: 67.7%\nH0: 31.5%\nH3: 0.5%\nH4: 0.2%\nH5: 0.1%\n\nVerdict: SUPPORTED",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=solid];
    h1 -> posterior_summary [style=dashed];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=dashed];
    h4 -> posterior_summary [style=dashed];
    h5 -> posterior_summary [style=dashed];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Scientific Cons\nH2: 67.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Vaccine Skeptic\nH0: 46.9%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Precautionary\nH0: 50.6%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 52\nEvidence Clusters: 5\nParadigms Analyzed: 3\nAvg Items/Cluster: 10.4\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH2: 60% → 67.7% (+7.7%)\nH0: 5% → 31.5% (+26.5%)\nH3: 10% → 0.5% (-9.5%)\n\nTotal WoE: 138.4 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 2 different conclusions across paradigms\n• 3 strong support signals\n• 12 strong refutation signals\n• Clear margin (36%) over alternatives",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H2\n(FALSE - No causal link exists)\n\nPosterior: 67.7%\nMargin over #2: 36.1%\nConfidence: Moderate\n\nStatus: SUPPORTED",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
