// BFIH Evidence Flow: Do vaccines cause autism in children?...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"Do vaccines cause autism in children?\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: OTHER - Unforeseen explanation\n\nPrior: 0.05\nPosterior: 0.019\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=1];

        h1 [label="H1: TRUE - Vaccines directly cause\nautism\n\nPrior: 0.05\nPosterior: 0.001\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h2 [label="H2: FALSE - No causal link exists\n\nPrior: 0.60\nPosterior: 0.727\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=3];

        h3 [label="H3: PARTIAL - Rare subgroup\nvulnerability\n\nPrior: 0.10\nPosterior: 0.097\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2.5];

        h4 [label="H4: PARTIAL - Indirect pathway via\nimmune activation\n\nPrior: 0.10\nPosterior: 0.094\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

        h5 [label="H5: PARTIAL - Timing/schedule effects\nonly\n\nPrior: 0.10\nPosterior: 0.062\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=1];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: Large epidemiology & meta-analyses\n(mostly null associations)\n(25 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="Large cohort/case-control studies\nand meta-analyses examining MMR,\nthimerosal/ethylmercury, antigen\n(25 items)\n\nWoE to H2: 2.2 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Institutional consensus & authoritative\nreviews\n(13 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="Consensus statements and major\nreviews (IOM/NASEM, CDC, AAP, WHO,\nBrookings expert commentary, and\n(13 items)\n\nWoE to H2: 2.8 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Wakefield episode: initial signal, then\nretraction/fraud analysis\n(5 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="The original small 1998 case\nseries suggesting a temporal\nassociation, followed by formal\n(5 items)\n\nWoE to H2: 2.4 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    subgraph cluster_c4 {
        label="C4: Mechanistic plausibility: immune\nactivation models\n(1 items)";
        style="filled,rounded";
        fillcolor="#E6F3FF";
        fontsize=10;

        c4_node [label="Animal/translational evidence that\nmaternal immune activation can\nproduce autism-like phenotypes,\n(1 items)\n\nWoE to H4: 2.1 dB",
                 shape=ellipse, style="filled", fillcolor="#CCE6FF"];
    }

    subgraph cluster_c5 {
        label="C5: Contrarian/contested evidence & ongoing\nreviews (VAERS/ecological signals,\npreprints, subgroup/timing claims,\npolicy noise)\n(8 items)";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=10;

        c5_node [label="Weaker/contested pro-link claims\n(VAERS-based analyses,\nsubgroup/timing reanalyses),\ncritiques\n(8 items)\n\nWoE to H1: 2.5 dB",
                 shape=ellipse, style="filled", fillcolor="#E6CCFF"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h1 [label="LR: 0.16", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h2 [label="LR: 1.65", color="#66CC66", penwidth=2, style=solid];
    c2_node -> h1 [label="LR: 0.24", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h2 [label="LR: 1.91", color="#66CC66", penwidth=2, style=solid];
    c3_node -> h1 [label="LR: 0.21", color="#CC0000", penwidth=1.5, style=dotted];
    c3_node -> h2 [label="LR: 1.73", color="#66CC66", penwidth=2, style=solid];
    c4_node -> h2 [label="LR: 0.80", color="#CC6666", penwidth=1, style=dotted];
    c4_node -> h4 [label="LR: 1.61", color="#66CC66", penwidth=2, style=solid];
    c5_node -> h1 [label="LR: 1.78", color="#66CC66", penwidth=2, style=solid];
    c5_node -> h2 [label="LR: 0.46", color="#CC0000", penwidth=1.5, style=dotted];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH2: 72.7%\nH3: 9.7%\nH4: 9.4%\nH5: 6.2%\nH0: 1.9%\n\nVerdict: STRONGLY SUPPORTED",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=dashed];
    h1 -> posterior_summary [style=dashed];
    h2 -> posterior_summary [style=solid];
    h3 -> posterior_summary [style=dashed];
    h4 -> posterior_summary [style=dashed];
    h5 -> posterior_summary [style=dashed];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Scientific Cons\nH2: 72.7%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Vaccine Skeptic\nH3: 31.0%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Precautionary\nH3: 31.2%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 52\nEvidence Clusters: 5\nParadigms Analyzed: 3\nAvg Items/Cluster: 10.4\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH2: 60% → 72.7% (+12.7%)\nH3: 10% → 9.7% (-0.3%)\nH4: 10% → 9.4% (-0.6%)\n\nTotal WoE: 62.1 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 2 different conclusions across paradigms\n• 0 strong support signals\n• 3 strong refutation signals\n• Decisive margin (63%) over alternatives",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H2\n(FALSE - No causal link exists)\n\nPosterior: 72.7%\nMargin over #2: 63.0%\nConfidence: High\n\nStatus: STRONGLY SUPPORTED",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
