// BFIH Evidence Flow: The most effective way to learn a new skill is through delib...
// Auto-generated visualization of Bayesian analysis structure

digraph BFIHEvidenceFlow {
    // Graph properties
    rankdir=LR;
    compound=true;
    fontname="Helvetica-Bold,Arial Bold,sans-serif";
    node [fontname="Helvetica,Arial,sans-serif", fontsize=10];
    edge [fontname="Helvetica,Arial,sans-serif", fontsize=8];
    label="BFIH Analysis of Proposition:\n\"The most effective way to learn a new skill is through\ndeliberate practice with immediate feedback\"";
    labelloc="t";
    fontsize=18;

    // ============================================================
    // Hypothesis Space
    // ============================================================

    subgraph cluster_hypotheses {
        label="Hypothesis Space (6 explanations)";
        style="filled,rounded";
        fillcolor="#E8F4F8";
        fontsize=12;
        penwidth=2;

        h0 [label="H0: Other/Unforeseen Alternatives\n\nPrior: 0.10\nPosterior: 0.047\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#E0E0E0", penwidth=1];

        h1 [label="H1: [Proposition TRUE] - Direct\nEfficacy of Deliberate Practice\n\nPrior: 0.15\nPosterior: 0.053\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#CCFFCC", penwidth=1];

        h2 [label="H2: [Proposition FALSE] - Multifaceted\nLearning Approaches Prevail\n\nPrior: 0.20\nPosterior: 0.071\nStatus: WEAK",
            shape=box, style="filled,rounded", fillcolor="#FF9999", penwidth=1];

        h3 [label="H3: [Proposition PARTIAL] -\nEffectiveness is Context-Dependent\n\nPrior: 0.25\nPosterior: 0.312\nStatus: STRONGEST",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=3];

        h4 [label="H4: [Domain-Specific] - Holistic,\nContextual Learning Advantages\n\nPrior: 0.15\nPosterior: 0.249\nStatus: MODERATE",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2];

        h5 [label="H5: [Domain-Specific] - Long-Term\nMastery Requires Reflective\nIteration\n\nPrior: 0.15\nPosterior: 0.267\nStatus: STRONG",
            shape=box, style="filled,rounded", fillcolor="#B3D9FF", penwidth=2.5];

    }

    // ============================================================
    // Evidence Clusters
    // ============================================================

    subgraph cluster_c1 {
        label="C1: Immediate Feedback and Deliberate\nPractice\n(30 items)";
        style="filled,rounded";
        fillcolor="#FFE6E6";
        fontsize=10;

        c1_node [label="This cluster aggregates evidence\nemphasizing the positive impact of\ndeliberate practice when\n(30 items)\n\nWoE to H1: 5.3 dB",
                 shape=ellipse, style="filled", fillcolor="#FFCCCC"];
    }

    subgraph cluster_c2 {
        label="C2: Alternative and Contrasting Practice\nModalities\n(23 items)";
        style="filled,rounded";
        fillcolor="#FFFCE6";
        fontsize=10;

        c2_node [label="This cluster contains evidence on\nalternative learning methods such\nas interleaved practice,\n(23 items)\n\nWoE to H2: 2.5 dB",
                 shape=ellipse, style="filled", fillcolor="#FFFFCC"];
    }

    subgraph cluster_c3 {
        label="C3: Reflective and Metacognitive Practice\n(8 items)";
        style="filled,rounded";
        fillcolor="#FFF0E6";
        fontsize=10;

        c3_node [label="This cluster includes evidence\nhighlighting the role of reflective\npractice and metacognitive\n(8 items)\n\nWoE to H5: 5.1 dB",
                 shape=ellipse, style="filled", fillcolor="#FFE6CC"];
    }

    // ============================================================
    // Evidence flows to hypotheses
    // ============================================================

    c1_node -> h0 [label="LR: 0.26", color="#CC0000", penwidth=1.5, style=dotted];
    c1_node -> h1 [label="LR: 3.40", color="#006600", penwidth=3, style=solid];
    c1_node -> h2 [label="LR: 0.24", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h1 [label="LR: 0.15", color="#CC0000", penwidth=1.5, style=dotted];
    c2_node -> h2 [label="LR: 1.78", color="#66CC66", penwidth=2, style=solid];
    c2_node -> h5 [label="LR: 0.31", color="#CC0000", penwidth=1.5, style=dotted];
    c3_node -> h2 [label="LR: 0.48", color="#CC0000", penwidth=1.5, style=dotted];
    c3_node -> h5 [label="LR: 3.26", color="#006600", penwidth=3, style=solid];

    // ============================================================
    // Posterior Summary
    // ============================================================

    posterior_summary [label="K0 Posteriors\n\nH3: 31.2%\nH5: 26.7%\nH4: 24.9%\nH2: 7.1%\nH1: 5.3%\n\nVerdict: UNCERTAIN",
                        shape=box, style="filled,rounded", fillcolor="#FFF4E6",
                        fontsize=11, penwidth=2];

    h0 -> posterior_summary [style=dashed];
    h1 -> posterior_summary [style=dashed];
    h2 -> posterior_summary [style=dashed];
    h3 -> posterior_summary [style=solid];
    h4 -> posterior_summary [style=solid];
    h5 -> posterior_summary [style=solid];

    // ============================================================
    // Paradigm Comparison
    // ============================================================

    subgraph cluster_paradigm_compare {
        label="Cross-Paradigm Comparison";
        style="filled,rounded";
        fillcolor="#F0E6FF";
        fontsize=11;

        paradigm_k0 [label="K0: Empirical Pract\nH3: 31.2%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k0_inv [label="K0-inv: Traditional Wis\nH4: 40.5%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k1 [label="K1: Cognitive Optim\nH1: 59.4%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k2 [label="K2: Long-term Devel\nH5: 39.3%", style="filled", fillcolor="#E6CCFF"];
        paradigm_k3 [label="K3: Standardized Tr\nH5: 32.7%", style="filled", fillcolor="#E6CCFF"];
    }

    posterior_summary -> paradigm_k0 [style=solid];
    posterior_summary -> paradigm_k0_inv [style=dashed];
    posterior_summary -> paradigm_k1 [style=dashed];
    posterior_summary -> paradigm_k2 [style=dashed];
    posterior_summary -> paradigm_k3 [style=dashed];

    // ============================================================
    // Evidence Base Assessment
    // ============================================================

    evidence_assessment [label="Evidence Assessment\n\nTotal Evidence Items: 61\nEvidence Clusters: 3\nParadigms Analyzed: 5\nAvg Items/Cluster: 20.3\n\nCoverage: Extensive",
                          shape=box, style="filled,rounded", fillcolor="#E6FFE6",
                          fontsize=10, penwidth=1.5];

    // ============================================================
    // Bayesian Synthesis
    // ============================================================

    bayesian_synthesis [label="BAYESIAN SYNTHESIS\n\nH3: 25% → 31.2% (+6.2%)\nH5: 15% → 26.7% (+11.7%)\nH4: 15% → 24.9% (+9.9%)\n\nTotal WoE: 54.5 dB",
                         shape=box, style="filled,rounded", fillcolor="#E6F3FF",
                         fontsize=10, penwidth=1.5];

    paradigm_k0 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k0_inv -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k1 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k2 -> bayesian_synthesis [style=dashed, color="#666666"];
    paradigm_k3 -> bayesian_synthesis [style=dashed, color="#666666"];

    // ============================================================
    // Key Insights
    // ============================================================

    key_insights [label="KEY INSIGHTS\n\n• 4 different conclusions across paradigms\n• 2 strong support signals\n• 4 strong refutation signals\n• Narrow margin (4%) - some uncertainty",
                   shape=box, style="filled,rounded", fillcolor="#FFF0F5",
                   fontsize=10, penwidth=1.5];

    bayesian_synthesis -> key_insights [style=solid];

    // ============================================================
    // Final Analysis Summary
    // ============================================================

    final_summary [label="ANALYSIS CONCLUSION\n\nLeading Hypothesis: H3\n([Proposition PARTIAL] -\nEffectiveness is\nContext-Dependent)\n\nPosterior: 31.2%\nMargin over #2: 4.5%\nConfidence: Low\n\nStatus: UNCERTAIN",
                    shape=box, style="filled,bold,rounded", fillcolor="#FFE4B5",
                    fontsize=11, penwidth=3];

    key_insights -> final_summary [style=solid, penwidth=2];

}
